context("test-pops")

test_that("Model stops if files don't exist or aren't the correct extension", {
  expect_equal(pops(infected_file = "", host_file =  system.file("extdata", "simple2x2", "total_plants.tif", package = "PoPS"), total_plants_file =  system.file("extdata", "simple2x2", "total_plants.tif", package = "PoPS")), "Infected file does not exist")
  expect_equal(pops(infected_file =  system.file("extdata", "simple2x2", "infected.csv", package = "PoPS"), host_file =  system.file("extdata", "simple2x2", "total_plants.tif", package = "PoPS"), total_plants_file =  system.file("extdata", "simple2x2", "total_plants.tif", package = "PoPS")), "Infected file is not one of '.grd', '.tif', '.img'")
  expect_equal(pops(infected_file =  system.file("extdata", "simple2x2", "infected.tif", package = "PoPS"), host_file = "", total_plants_file =  system.file("extdata", "simple2x2", "total_plants.tif", package = "PoPS")), "Host file does not exist")
  expect_equal(pops(infected_file =  system.file("extdata", "simple2x2", "infected.tif", package = "PoPS"), host_file =  system.file("extdata", "simple2x2", "infected.csv", package = "PoPS"), total_plants_file =  system.file("extdata", "simple2x2", "total_plants.tif", package = "PoPS")), "Host file is not one of '.grd', '.tif', '.img'")
  expect_equal(pops(infected_file =  system.file("extdata", "simple2x2", "infected.tif", package = "PoPS"), host_file =  system.file("extdata", "simple2x2", "total_plants.tif", package = "PoPS"), total_plants_file = ""), "Total plants file does not exist")
  expect_equal(pops(infected_file =  system.file("extdata", "simple2x2", "infected.tif", package = "PoPS"), host_file =  system.file("extdata", "simple2x2", "total_plants.tif", package = "PoPS"), total_plants_file =  system.file("extdata", "simple2x2", "infected.csv", package = "PoPS")), "Total plants file is not one of '.grd', '.tif', '.img'")
  expect_equal(pops(infected_file =  system.file("extdata", "simple2x2", "infected.tif", package = "PoPS"), host_file =  system.file("extdata", "simple2x2", "total_plants.tif", package = "PoPS"), total_plants_file =  system.file("extdata", "simple2x2", "total_plants.tif", package = "PoPS"), use_lethal_temperature = TRUE, temperature_file = ""), "Temperature file does not exist")
  expect_equal(pops(infected_file =  system.file("extdata", "simple2x2", "infected.tif", package = "PoPS"), host_file =  system.file("extdata", "simple2x2", "total_plants.tif", package = "PoPS"), total_plants_file =  system.file("extdata", "simple2x2", "total_plants.tif", package = "PoPS"), use_lethal_temperature = TRUE, temperature_file =  system.file("extdata", "simple2x2", "infected.csv", package = "PoPS")), "Temperature file is not one of '.grd', '.tif', '.img'")
  expect_equal(pops(infected_file =  system.file("extdata", "simple2x2", "infected.tif", package = "PoPS"), host_file =  system.file("extdata", "simple2x2", "total_plants.tif", package = "PoPS"), total_plants_file =  system.file("extdata", "simple2x2", "total_plants.tif", package = "PoPS"), temp = TRUE, temperature_coefficient_file = ""), "Temperature coefficient file does not exist")
  expect_equal(pops(infected_file =  system.file("extdata", "simple2x2", "infected.tif", package = "PoPS"), host_file =  system.file("extdata", "simple2x2", "total_plants.tif", package = "PoPS"), total_plants_file =  system.file("extdata", "simple2x2", "total_plants.tif", package = "PoPS"), temp = TRUE, temperature_coefficient_file =  system.file("extdata", "simple2x2", "infected.csv", package = "PoPS")), "Temperature coefficient file is not one of '.grd', '.tif', '.img'")
  expect_equal(pops(infected_file =  system.file("extdata", "simple2x2", "infected.tif", package = "PoPS"), host_file =  system.file("extdata", "simple2x2", "total_plants.tif", package = "PoPS"), total_plants_file =  system.file("extdata", "simple2x2", "total_plants.tif", package = "PoPS"), precip = TRUE, precipitation_coefficient_file = ""), "Precipitation coefficient file does not exist")
  expect_equal(pops(infected_file =  system.file("extdata", "simple2x2", "infected.tif", package = "PoPS"), host_file =  system.file("extdata", "simple2x2", "total_plants.tif", package = "PoPS"), total_plants_file =  system.file("extdata", "simple2x2", "total_plants.tif", package = "PoPS"), precip = TRUE, precipitation_coefficient_file =  system.file("extdata", "simple2x2", "infected.csv", package = "PoPS")), "Precipitation coefficient file is not one of '.grd', '.tif', '.img'")

})

test_that("Model stops if parameters are of the wrong type and/or dimension", {
  expect_equal(pops(infected_file = system.file("extdata", "simple2x2", "infected.tif", package = "PoPS"), host_file = system.file("extdata", "simple2x2", "total_plants.tif", package = "PoPS"), total_plants_file = system.file("extdata", "simple2x2", "total_plants.tif", package = "PoPS"), time_step = "two"), "Time step must be one of 'week', 'month' or 'day'")
