  expect_equal(pops(infected_file = system.file("extdata", "simple2x2", "infected.tif", package = "PoPS"), host_file = system.file("extdata", "simple2x2", "total_plants.tif", package = "PoPS"), total_plants_file = system.file("extdata", "simple2x2", "total_plants.tif", package = "PoPS"), start_time = "five"), "End time and/or start time not of type numeric and/or in format YYYY")
  expect_equal(pops(infected_file = system.file("extdata", "simple2x2", "infected.tif", package = "PoPS"), host_file = system.file("extdata", "simple2x2", "total_plants.tif", package = "PoPS"), total_plants_file = system.file("extdata", "simple2x2", "total_plants.tif", package = "PoPS"), start_time = 19), "End time and/or start time not of type numeric and/or in format YYYY")

})

